(370, '2', '3', 'A', 'FK322', 'EJ9401', '<ol>\r\n	<li>a.&nbsp;&nbsp; Tentukan jenis fungsi (genap, ganjil, bukan keduanya) dari : y = 7x2 + 5x + 3&nbsp;</li>\r\n</ol>\r\n\r\n<p>b.&nbsp; Hitung nilai x dari persamaan -5<strong></strong>x-7<strong></strong> + 2 = -13</p>\r\n\r\n<p>2.&nbsp;&nbsp; Carilah limit berikut :</p>\r\n\r\n<p>a.&nbsp; lim<em>x&rarr;3</em><em>x</em><em>3</em><em>-27</em><em>x</em><em>2</em><em>-9</em>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;c. lim<em>x&rarr;&infin;</em><em>2x</em><em>2</em><em>+2x-3</em><em>-</em><em>2x</em><em>2</em><em>-2x-3</em><em>2</em></p>\r\n\r\n<p>b.&nbsp; lim<em>x&rarr;9</em><em>x-9</em><em>x</em><em>-3</em> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; d. lim<em>x&rarr;&infin;</em><em>5x</em><em>3</em><em>+7x-10</em><em>x+4</em></p>\r\n\r\n<p>3.&nbsp; Turunkanlah fungsi-fungsi berikut:</p>\r\n\r\n<p>a.&nbsp; y=2x+13x-14&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;c.&nbsp; <em>y=</em>cos<em>x</em><em>x</em></p>\r\n\r\n<p>b.&nbsp; y=3x+162x-5 &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; d. y = sin 3x + cos 2x</p>\r\n\r\n<p>4.&nbsp; Carilah titik-titik ekstrim dari <em>y=</em><em>x</em><em>3</em><em>3</em><em>+</em><em>x</em><em>2</em><em>2</em><em>-6x+8</em> &nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>5.&nbsp; Turunkanlah fungsi berikut :</p>\r\n\r\n<p>a.&nbsp;&nbsp; y = alog(3x2 &ndash; 7)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; c. <em>y=</em><em>e</em><em>x</em><em>n</em></p>\r\n\r\n<p>b.&nbsp; y = ln (x3 +2)(x2 + 3)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; d.&nbsp; y = ln (x2- 3x +1)</p>\r\n\r\n<p>6.&nbsp; Carilah y&rsquo; dari fungsi-fungsi berikut :</p>\r\n\r\n<p>a.&nbsp;&nbsp; x3-4x2y-3y-1=0 &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; b.&nbsp; x3y+xy3=10 &nbsp;di titik (1. -1)</p>\r\n', '', '2022/2023', '3', '1', '2022-11-08 05:59:52', '0000-00-00 00:00:00'),
(371, '2', '3', 'B', 'FK322', 'EJ9401', '<p>1. a.&nbsp;&nbsp; Tentukan apakah fungsi-fungsi di bawah ini merupakan fungsi genap, fungsi ganjil atau bukan keduanya :&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;f(x) = x3 &ndash; x7&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;b.&nbsp; Jika g(x) = x+4 dan (gof)(x)= x+4 &nbsp;&nbsp;&nbsp; Berapakah f(5)?</p>\r\n\r\n<p>2.&nbsp;&nbsp; Carilah limit berikut :</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; a.&nbsp;&nbsp; lim<em>x&rarr;4</em><em>x-4</em><em>x</em><em>2</em><em>-x-12</em> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; c. lim<em>x&rarr;&infin;</em><em>x</em><em>2</em><em>+3x-8</em><em>-</em><em>2x</em><em>2</em><em>+x+5</em><em>4</em></p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp;b. lim<em>x&rarr;1</em><em>x</em><em>2</em><em>+x-2</em><em>(x-1)</em><em>2</em> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; d. limx&rarr;&infin;5x2+x-22x4+x+4</p>\r\n\r\n<p>3.&nbsp; Turunkanlah fungsi-fungsi berikut:</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; a. &nbsp;y=12x2+4x &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; c. &nbsp;y=sinx2-cosx</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; b.&nbsp; y=35x2-x+4 &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; d. &nbsp;y = x2 sin x</p>\r\n\r\n<p>4.&nbsp; Tentukan titik ekstrik (maksimum/minimum) dari : y=x2+250x</p>\r\n\r\n<p>5.&nbsp; Turunkanlah fungsi berikut :</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;a.&nbsp;&nbsp; y = alog(3x2 &ndash; 7)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; c.&nbsp; y = e-x ln x</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;b.&nbsp; &nbsp; &nbsp;y=ln4(3x-4)2 &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; d.&nbsp; y = x2 3x</p>\r\n\r\n<p>6.&nbsp; Carilah y&rsquo; dari fungsi-fungsi berikut :</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; a.&nbsp;&nbsp; y4+3y-4x3=5x+1 &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; b.&nbsp; <em>4xy</em><em>3</em><em>-</em><em>2x</em><em>2</em><em>y+</em><em>x</em><em>3</em><em>-5x+6=0&nbsp;&nbsp; di y=-2</em> &nbsp;&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', '', '2022/2023', '3', '1', '2022-11-08 06:05:06', '0000-00-00 00:00:00'),
