(795, '1', '7', 'C', 'IS215', 'BW1904', '<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p>Kerjakan Soal Berikut!</p>\r\n\r\n			<ol>\r\n				<li>Jelaskan dan sebutkan perbedaan perkembangan WEB 1.0 sampai 4.0</li>\r\n				<li>Apakah yang akan terjadi jika perkembangan WEB sudah sampai WEB 5.0?</li>\r\n				<li>Bagaimana anda menjelaskan tentang pentingnya ICT?</li>\r\n				<li>Apakah yang dimaksud dengan Transformasi Digital? sebutkan contohnya?</li>\r\n				<li>Apa yang dimaksud dengan Etika didalam Dunia Maya (Cyber Ethics)</li>\r\n			</ol>\r\n\r\n			<p>&nbsp;Ketentuan:</p>\r\n\r\n			<ol>\r\n				<li>Upload jawaban dalam format PDF</li>\r\n				<li>Upload file ke cbt.unbin.ac.id</li>\r\n				<li>Isi form dan upload file ke https://docs.google.com/forms/d/e/1FAIpQLSdX4wTfh6n5VgIbJFinroI1S7bRBgwUFd1_rbyG_neVcTfM9Q/viewform?usp=share_link</li>\r\n			</ol>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '', '2022/2023', '4', '1', '2022-12-23 11:48:49', '0000-00-00 00:00:00'),
(801, '1', '5', 'C', 'GC225', 'DS1008', '<p><strong>Jawab pertanyaan dibawah ini</strong></p>\r\n\r\n<ol>\r\n	<li><strong>Apa yang dimaksud dengan Distributed sytem</strong><strong>. </strong></li>\r\n	<li><strong>Sebutkan Komponen &nbsp;utama apa saja yang membuat distributed system dapat dilakukan. </strong></li>\r\n	<li><strong>Apa &nbsp;alasan utama distributed system dilakukan </strong></li>\r\n	<li><strong>unsur apa saja yang&nbsp; terdistribusi dala DS.</strong></li>\r\n</ol>\r\n', '', '2022/2023', '4', '1', '2022-12-23 09:25:15', '0000-00-00 00:00:00'),
(802, '1', '5', 'C', 'GC225', 'DS1008', '<p><strong>Dalam</strong><strong> Distributed system </strong></p>\r\n\r\n<ol>\r\n	<li><strong>Apa yang dimaksud dengan jaringan komputer.</strong></li>\r\n	<li><strong>Untuk apa jaringan komputer diperlukan.</strong></li>\r\n	<li><strong>Berikan 3 contoh jenis jaringan komputer yang anda tahu</strong></li>\r\n	<li><strong>Apa yang dimaksud dentgn hub atau switch dalam jaringan </strong></li>\r\n	<li><strong>Apa yang dimaksud dengan bridge dalam jaringan </strong></li>\r\n</ol>\r\n', '', '2022/2023', '4', '1', '2022-12-23 09:26:29', '0000-00-00 00:00:00'),
(803, '1', '5', 'C', 'GC225', 'DS1008', '<p><strong>Dalam distributed system dukungan Operating system sangat diperlukan&rsquo;</strong></p>\r\n\r\n<ol>\r\n	<li><strong>Sebutkan apa fungsi utama OS. Dan berikan contoh&nbsp; OS yang ada. </strong></li>\r\n	<li><strong>apa yang dimaksud dengan MIdleware dan apa fungsinya, serta berikan contohnya.</strong></li>\r\n</ol>\r\n', '', '2022/2023', '4', '1', '2022-12-23 09:27:05', '0000-00-00 00:00:00'),
(804, '1', '5', 'C', 'GC225', 'DS1008', '<p><strong>Jelaskan</strong><strong> </strong></p>\r\n\r\n<ol>\r\n	<li><strong>Apa yang dimaksud dengan routing</strong></li>\r\n	<li><strong>Jelasan prinsip dasar &nbsp;routing dengan algoritma spanning tree; </strong></li>\r\n</ol>\r\n', '', '2022/2023', '4', '1', '2022-12-23 09:27:41', '0000-00-00 00:00:00'),
(805, '1', '5', 'C', 'GC225', 'DS1008', '<p><strong>Dalam distributed system, issue penting adalah keamanan system.</strong><strong> </strong></p>\r\n\r\n<ol>\r\n	<li><strong>apa yang dimaksud dengan anacaman atau threats dalam distributed sistem</strong></li>\r\n	<li><strong>berikan contoh ancaman yang ada </strong></li>\r\n	<li><strong>bagaimana menghidarkan atau mengatasi ancaman</strong></li>\r\n</ol>\r\n', '', '2022/2023', '4', '1', '2022-12-23 09:28:12', '0000-00-00 00:00:00'),
